import axios from "axios";
import { useState, useEffect } from "react";
import {
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
} from "@mui/material";

const ShowPets = () => {
  const [pets, setPets] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api/pets/all")
      .then((res) =>{setPets(res.data)})

  }, []);
  return (
    <>
    <div
      style={{
        backgroundColor: "white",
      }}
      >
      <Container
        sx={{
          p: 1,
          mb: 1,
          backgroundColor: "#e0e0e0",
          borderRadius: 1,
          color: "action.active",
          fontWeight: "bold",
        }}
        >
        <p style={{ textAlign: "center", color: "black"}}>Mascotas</p>
      </Container>
      <Container sx={{ p: 5, backgroundColor: "#e0e0e0", borderRadius: 1 }}>
        <Grid container my={4}>
          {pets?.map((mascota) => {
            return(
            <Link to={`/`} key={fundacion._id}>
              <Grid item xs={12} p={2} key={mascota._id}>
                <Card>
                  <CardMedia>
                    <img src={mascota.photos[0]} alt="" width='100%'/>
                  </CardMedia>
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {mascota.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {mascota.specie}
                    </Typography>
                    <Typography variant="body4" color="text.secondary">
                      {mascota.gender}
                    </Typography>
                    {/* <Typography variant="body2" color="text.secondary">
                      {mascota.foundation}
                    </Typography> */}
                  </CardContent>
                </Card>
              </Grid>
            </Link>
            )
          })}
        </Grid>
      </Container>
    </div>
    </>
  );
};

export default ShowPets;
