import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { Box, CardActionArea } from "@mui/material";
import { Stack } from "@mui/system";

import { Link } from "react-router-dom";
import { yellow } from "@mui/material/colors";
import { useDispatch, useSelector } from "react-redux";
import { get_genres } from "../state/genre";

const color = yellow[500];
export default function SimpleSlider() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(get_genres());
  }, []);
  const genres = useSelector((state) => state.genre);

  const baseUrl = "https://images.igdb.com/igdb/image/upload/t_720p/";

  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 10,
    slidesToScroll: 9,
    responsive: [
      {
        breakpoint: 1440,
        settings: {
          slidesToShow: 7,
          slidesToScroll: 7,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 1040,
        settings: {
          slidesToShow: 5,
          slidesToScroll: 5,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <Slider {...settings}>
      {!genres.length ? (
        <div>Loading...</div>
      ) : (
        genres.map((genre, i) => {
          return (
            <Box key={i} spacing={2}>
              <div key={genre.id}>
                <h3 key={genre.id}>
                  <Card sx={{ maxWidth: 150, maxHeight: 280 }}>
                    <CardActionArea>
                      {genre.products[0] && (
                        <CardMedia
                          component="img"
                          height="200"
                          image={baseUrl + genre.products[0].urlId + ".jpg"}
                          alt="Genre_img"
                        />
                      )}

                      <Link
                        style={{ textDecoration: "none" }}
                        to={`/products/${genre.genre}`}
                      >
                        <CardContent
                          component={Stack}
                          direction="column"
                          justifyContent="center"
                          sx={{
                            bgcolor: "secondary.main",
                            height: 80,
                          }}
                        >
                          <Typography
                            color="text.main"
                            gutterBottom
                            variant="subtitle2"
                            component="div"
                            borderRadius={5}
                          >
                            {genre.genre.toUpperCase()}
                          </Typography>
                        </CardContent>
                      </Link>
                    </CardActionArea>
                  </Card>
                </h3>
              </div>
            </Box>
          );
        })
      )}
    </Slider>
  );
}
