import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import SearchIcon from "@mui/icons-material/Search";
import InputBase from "@mui/material/InputBase";
import { Link, useNavigate } from "react-router-dom";
import {
  Button,
  Grid,
  ImageListItem,
  ListItemIcon,
  Stack,
} from "@mui/material";
import logo from "../assets/logoCeibo.png";
import Home from "@mui/icons-material/Home";
import Pets from "@mui/icons-material/Pets";
import Profile from "@mui/icons-material/Person";
import Favorite from "@mui/icons-material/StarRate";
import History from "@mui/icons-material/History";
import Message from "@mui/icons-material/Message";
import Footer from "./Footer";
import {useSelector} from 'react-redux'
import useMatches from "../hooks/useMatches";

let loginMenu;

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha((theme.palette.color = "#FFD600"), 0.15),
  "&:hover": {
    backgroundColor: alpha((theme.palette.color = "#FFD600"), 0.25),
  },
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(1),
    width: "auto",
  },
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  borderRadius: 20,
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(0)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "12ch",
      "&:focus": {
        width: "20ch",
      },
    },
  },
}));

const drawerWidth = 300;

const Main = styled("main", { shouldForwardProp: (prop) => prop !== "open" })(
  ({ theme, open }) => ({
    flexGrow: 1,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: `-${drawerWidth}px`,
    ...(open && {
      transition: theme.transitions.create("margin", {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
      marginLeft: 0,
    }),
  })
);

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  transition: theme.transitions.create(["margin", "width"], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled("div")(({ theme }) => ({
  display: "flex",
  alignItems: "center",
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: "flex-end",
}));

export default function PersistentDrawerLeft({ prop }) {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [search, setSearch] = React.useState("");
  const user = useSelector((state)=>state.user)
  const navigate = useNavigate();
  console.log(user)

  //false = mobile  ---  true = desktop
  const matches = useMatches()

  //style variables
  let AppBarStyle 
  let DrawerHeader
  let DrawerList
  let SearchStyle
  let ToolbarStyle
  let IconButtonStyle
  let UserNameStyle
  let logoStyle

  //desktop or mobile
  if(matches){}
  else{
    logoStyle = { paddingLeft: 0,width:'100%' ,justifyContent: "center"}
    UserNameStyle = {display:'flex',justifyContent:'center', width:'100%'}
    ToolbarStyle= { paddingRight: 0, maxHeight: 67, display:'flex' }
    IconButtonStyle = { mr: 2, ...(open && { display: "none" }) }
    AppBarStyle = {
      position:"fixed",
      backgroundColor: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#FFD600")
          : (theme.palette.color = "#FFD600"),
      color: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#FFFFFF")
          : (theme.palette.color = "#FFFFFF"),
    }
    SearchStyle = {
      borderRadius: 10,
      backgroundColor: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#F1F2F1")
          : (theme.palette.color = "#1e244b"),
      color: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#000000")
          : (theme.palette.color = "#000000"),
    }
    DrawerList = {
      height: "100%",
      backgroundColor: (theme) =>
        theme.palette.mode === "dark"
          ? (theme.palette.color = "#FFD600")
          : (theme.palette.color = "#FFD600"),
      color: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#1e244b")
          : (theme.palette.color = "#04092A"),
    }
    DrawerHeader = {
      backgroundColor: (theme) =>
        theme.palette.mode === "dark"
          ? (theme.palette.color = "##FFD600")
          : (theme.palette.color = "#FFD600"),
      color: (theme) =>
        theme.palette.mode === "light"
          ? (theme.palette.color = "#000000")
          : (theme.palette.color = "#FFFFFF"),
    }
  }
  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate(`/search?query=${search}`);
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const handleLogOut = () => {};

  !user
    ? (loginMenu = (
        <>
          <Divider/>
          <Grid container>
            <Grid item>
              <Button
                onClick={() => {
                  navigate("/login");
                }}
                sx={DrawerList}
              >
                Iniciar Sesion
              </Button>
            </Grid>
            <Grid item>
              <Button
                onClick={() => {
                  navigate("/register");
                }}
                sx={DrawerList}
              >
                Registrarse
              </Button>
            </Grid>
          </Grid>
        </>
      ))
    : (loginMenu = (
        <>
          <Link
            style={{ color: "inherit", textDecoration: "none" }}
            to={"/profile"}
          >
            <ListItem disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  <Profile />
                </ListItemIcon>
                <ListItemText primary={"Perfil"} />
              </ListItemButton>
            </ListItem>
          </Link>
          <Link
            style={{ color: "inherit", textDecoration: "none" }}
            to={"/favorites"}
          >
            <ListItem disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  <Favorite />
                </ListItemIcon>
                <ListItemText primary={"Favoritos"} />
              </ListItemButton>
            </ListItem>
          </Link>
          <Link
            style={{ color: "inherit", textDecoration: "none" }}
            to={"/history"}
          >
            <ListItem disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  <History />
                </ListItemIcon>
                <ListItemText primary={"Historial"} />
              </ListItemButton>
            </ListItem>
          </Link>
          <Link
            style={{ color: "inherit", textDecoration: "none" }}
            to={"/messages"}
          >
            <ListItem disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  <Message />
                </ListItemIcon>
                <ListItemText primary={"Mensajes"} />
              </ListItemButton>
            </ListItem>
          </Link>
          <Stack>
            <Divider/>
            <Button onClick={handleLogOut} sx={DrawerList}>
              Cerrar Sesion
            </Button>
          </Stack>
        </>
      ));

  return (
    <>
      <Box sx={{ display: "flex" }}>
        <CssBaseline />
        <AppBar open={open} sx={AppBarStyle}>
          <Toolbar
            style={{ color: "black" }}
            sx={ToolbarStyle}
          >
            <IconButton
              color="inherit"
              aria-label="open drawer"
              onClick={handleDrawerOpen}
              edge="start"
              sx={IconButtonStyle}
            >
              <MenuIcon />
            </IconButton>
            <Box sx={{ display: "flex", flexDirection: "row" }}>
              <Search sx={SearchStyle} id='searchDisplay'>
                <StyledInputBase
                  id="search"
                  placeholder="Buscarâ€¦"
                  inputProps={{ "aria-label": "search" }}
                  onChange={handleSearch}
                />
              </Search>
              <Button sx={{ padding: 0 }} onClick={handleSubmit}>
                <SearchIcon sx={{ color: "black" }} />
              </Button>
            </Box>
            <Button
              onClick={() => {
                navigate("/");
              }}
              sx={logoStyle}
            >
              <ImageListItem
                sx={{ padding: 0, maxWidth: 56}}
              >
                <img alt="" src={logo} loading="lazy" />
              </ImageListItem>
            </Button>
          </Toolbar>
        </AppBar>
        <Drawer
          sx={{
            width: drawerWidth,
            flexShrink: 0,
            "& .MuiDrawer-paper": {
              width: drawerWidth,
              boxSizing: "border-box",
            },
          }}
          variant="persistent"
          anchor="left"
          open={open}
        >
          <DrawerHeader sx={DrawerHeader}>
            <Typography sx={UserNameStyle}>{`${user.name}`}</Typography>
            <Divider
              sx={{ backgroundColor: "tan" }}
              orientation="vertical"
              variant="middle"
              flexItem
            />
            <IconButton onClick={handleDrawerClose}>
              {theme.direction === "ltr" ? (
                <ChevronLeftIcon />
              ) : (
                <ChevronRightIcon />
              )}
            </IconButton>
          </DrawerHeader>
          <Divider sx={{ backgroundColor: "tan" }} />
          <List sx={DrawerList}>
            {["Mascotas", "Fundaciones"].map((text, i) => (
              <Link
                style={{ color: "inherit", textDecoration: "none" }}
                to={`/${text.toLowerCase()}`}
                key={i}
              >
                <ListItem key={text} disablePadding>
                  <ListItemButton>
                    <ListItemIcon>
                      {
                        <>
                          {i === 0 ? <Pets /> : <></>}
                          {i === 1 ? <Home /> : <></>}
                        </>
                      }
                    </ListItemIcon>
                    <ListItemText primary={text} />
                  </ListItemButton>
                </ListItem>
              </Link>
            ))}
            {loginMenu}
          </List>
        </Drawer>
        <Main /* open={open} */>
          <DrawerHeader />
          {prop}
        </Main>
      </Box>
      <Footer />
    </>
  );
}
