import { CardMedia } from '@mui/material';
import axios from 'axios';
import React, { useState } from 'react';
import { useEffect } from 'react';
import { Carousel } from 'react-responsive-carousel';

const Carrusel = ({foundation, foundationId}) => {

    const [pets, setPets] = useState([])

    useEffect(()=>{
        axios
            .get(`http://localhost:3001/api/foundation/${foundationId}/pets`)
            .then((pets)=>{setPets(pets.data)})

    },[])

    const createCarouselItemImage = (pet, i) => {

        console.log(pet)

        return (
            <div key={i} >
                <CardMedia sx={{width:''}}>

                <img src={pet.photos[0]} />
                <p className="legend">Legend {i}</p>
                </CardMedia>
            </div>
        )
    }

    const baseChildren = <div>{pets?.map(createCarouselItemImage)}</div>;

    return (
        <Carousel>{baseChildren.props.children}</Carousel>
    )
}

export default Carrusel
