import axios from "axios";
import { useState, useEffect } from "react";
import {
  Typography,
  Container,
  Grid,
  Card,
  CardContent,
  Button,
  CardActions
} from "@mui/material";
import { Link, useLocation } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { setUser } from "../state/user";

const ShowFavorites = () => {
  const {pathname} = useLocation()
  const dispatch = useDispatch()
  const [favorites, setFavorites] = useState([]);
  const user = useSelector(state=>state.user)

  useEffect(() => {
    axios
    .get("http://localhost:3001/api/user/me", {
      withCredentials: true,
      credentials: "include",
    })
    .then((resp) => {
      dispatch(setUser(resp.data))
    })
    .then(()=>{
      axios
        .get(`http://localhost:3001/api/user/favorites/${user?._id}`)
        .then((res) => {
          setFavorites(res.data)
          console.log(res.data)
        })
    })

  }, []);


  return (
    <>
    <div
      style={{
        backgroundColor: "white",
      }}
      >
      <Container sx={{ p: 5, backgroundColor: "#e0e0e0", borderRadius: 1 }}>
        <Typography variant="h2" sx={{pl:3}}>Favoritos</Typography>
        <Grid container my={4}>
        {favorites?.map((favoritos,i) => {
          return (
            <Link to={`/mascotas/${mascota._id}`} key={mascota._id} style={{textDecoration:'none', margin:'0px auto', minWidth:295}}>
              <Grid item xs={12} p={2} key={mascota._id}>
                <Card>
                  <CardMedia>
                    <img src={mascota.photos[0]} alt="" width='100%'/>
                  </CardMedia>
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                      {mascota.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                    {mascota.specie === 'perro' ? <img src={logoPerrito} width="50" height="50" alt="perrito"   />:<img src={logoGatito} width="40" height="40"  alt="gatito" />}</Typography>
                    <Typography variant="body4" color="text.secondary">
                      {mascota.gender === 'hembra'?<FemaleIcon sx={{width:40, height:40}}/>:<MaleIcon sx={{width:40, height:40}}/>}
                    </Typography>
                    {/* <Typography variant="body2" color="text.secondary">
                      {mascota.foundation}
                    </Typography> */}
                  </CardContent>
                </Card>
              </Grid>
            </Link>
          )
           })}
        </Grid>
      </Container>
    </div>
    </>
  );
};

export default ShowFavorites;
