import * as React from "react";
import {
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  Typography,
  CssBaseline,
  Box,
  Avatar,
  Stack,
  Container,
  CardMedia
} from "@mui/material";
import { useState } from "react";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { useNavigate } from "react-router";
import axios from "axios";
import { useSelector, useDispatch } from "react-redux";
import useMatches from "../hooks/useMatches";
import { setUser } from "../state/user";

const theme = createTheme();

const Form = () => {
  const { user } = useSelector((state) => state);
  const [form_data, set_form_data] = useState();

  const handleSubmit = async (e) => {
      e.preventDefault();
      await axios.put(`http://localhost:3001/api/user/update/${user._id}`, form_data)
  }

  const send_image = (files) => {
      const formData = new FormData();
      formData.append('file', files)
      set_form_data(formData)
      console.log(formData)
  }

  return (
      <Container>
          <form onSubmit={handleSubmit}>
              <input 
                  type="file" 
                  id="image_file"
                  onChange={(e) => {
                      send_image(e.target.files[0])
                  }}
              />
              <label htmlFor="image_file">
                  <span>Select Image</span>
              </label>
              <button>Save Image</button>
          </form>
  </Container>
);
}

const Profile = () => {
  const [open, setOpen] = useState(false);
  const { user } = useSelector((state) => state);
  const [name, setName] = useState(user.name);
  const [lastName, setLastName] = useState(user.last_name);
  const [email, setEmail] = useState(user.email);
  const [image, setImage] = useState();
  const seleccionArchivos = document.querySelector("#seleccionArchivos");
  const imagenPrevisualizacion = document.querySelector(
    "#imagenPrevisualizacion"
  );

  const navigate = useNavigate();
  const dispatch = useDispatch()

  const handleClickOpen = () => {
    setOpen(true);
  };
  const nameChange = (e) => {
    setName(e.target.value);
  };

  const lastNameChange = (e) => {
    setLastName(e.target.value);
  };

  const emailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSend = () => {
    axios
      .put(`http://localhost:3001/api/user/update/${user._id}`, {
        name: name,
        last_name: lastName,
        email: email,
      })
      .then((res) => dispatch(setUser(res.data)));
      setOpen(false);
  };

console.log(user.image);

  const handleImage = (files) => {
    const formData = new FormData();
    formData.append('file', files)
    setImage(formData)
    console.log(formData)
  }

  const handleSubmit = () => {
    axios
      .put(`http://localhost:3001/api/user/update/${user._id}`, {
        profile_picture: image,
        name: user.name,
        last_name: user.last_name,
        email: user.email
      })
      .then((res) => console.log(res.data));
  };

  //false = mobile  ---  true = desktop
  const matches = useMatches();

  if (matches) {} 
  else {}

  return (
    <>
      <ThemeProvider theme={theme}>
        <Container component="main" maxWidth="xs">
          <CssBaseline />
          <Box
            sx={{
              marginTop: 4,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            <Typography component="h1" variant="h5">
              Mi Perfil
            </Typography>
            <Stack direction="row" alignItems="center" spacing={2}>
              <Avatar  alt="Remy Sharp"
                sx={{ m: 2, width: 66, height: 66 }}>
                  <img id="imagenPrevisualizacion" alt="" width={"100%"} src={user.profile_picture} />
                </Avatar>
            </Stack>
            <Stack direction="row" alignItems="center" spacing={2}>
              <Button variant="contained" component="label">
                Subir imagen
                <input hidden id="seleccionArchivos" accept="image/*" type="file" onChange={handleImage} />
              </Button>
                <img id="imagenPrevisualizacion" alt="" />
            </Stack>
            <Box component="form" noValidate sx={{ mt: 2 }}>
              <Button
                variant="contained"
                onClick={handleClickOpen}
                fullWidth
                sx={{
                  marginBottom: 1,
                  marginTop: 2,
                  backgroundColor: "#03A696",
                  "&:hover": {
                    backgroundColor: "#04BF9D",
                    color: "#757575",
                  },
                }}
              >
                Editar datos personales
              </Button>
              <Dialog
                open={open}
                onClose={handleSend}
                maxWidth="md"
                fullWidth={true}
              >
                <DialogContent>
                  <TextField
                    onChange={nameChange}
                    label="Nombre"
                    defaultValue={user.name}
                    autoFocus
                    margin="dense"
                    id="name"
                    type="text"
                    fullWidth
                    variant="standard"
                  />
                  <TextField
                    onChange={lastNameChange}
                    label="Apellido"
                    defaultValue={user.last_name}
                    autoFocus
                    margin="dense"
                    id="name"
                    type="text"
                    fullWidth
                    variant="standard"
                  />
                  <TextField
                    onChange={emailChange}
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Email"
                    defaultValue={user.email}
                    type="text"
                    fullWidth
                    variant="standard"
                  />
                </DialogContent>

                <DialogActions>
                  <Button variant="contained" onClick={() => setOpen(false)}>
                    Cancelar
                  </Button>
                  <Button variant="contained" onClick={handleSend}>
                    Guardar cambios
                  </Button>
                </DialogActions>
              </Dialog>

              <Button
                type="submit"
                variant="contained"
                fullWidth
                size="large"
                sx={{
                  marginBottom: 1,
                  backgroundColor: "#03A696",
                  "&:hover": {
                    backgroundColor: "#04BF9D",
                    color: "#757575",
                  },
                }}
              >
                Cambiar contraseña
              </Button>
              <Button
                type="submit"
                variant="contained"
                fullWidth
                size="large"
                sx={{
                  marginBottom: 1,
                  backgroundColor: "#03A696",
                  "&:hover": {
                    backgroundColor: "#04BF9D",
                    color: "#757575",
                  },
                }}
                onClick={() => {
                  navigate("/adoptionForm");
                }}
              >
                Formulario de adopción
              </Button>
            </Box>
          </Box>
          <Button
            color="inherit"
            fullWidth
            sx={{ mt: 5, bgcolor: "#FFD640", mb: 1, borderRadius: 7 }}
            onClick={handleSubmit}
          >
            Guardar cambios
          </Button>
          <Button
            color="inherit"
            fullWidth
            sx={{ bgcolor: "#FFD640", mb: 1, borderRadius: 7 }}
            onClick={() => {
              navigate("/");
            }}
          >
            Volver
          </Button>
          <br />
          <br />
          <br />
        </Container>
      </ThemeProvider>
    </>
  );
};

export default Profile;
