import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useNavigate } from 'react-router';
import { useDispatch } from 'react-redux';
import { sendLoginRequest } from '../state/user';
import { useState } from 'react';

const theme = createTheme();

export default function SignUp() {

  const navigate = useNavigate()
  const dispatch = useDispatch()

  const [titulo, setTitulo] = useState('')
  const [leyenda, setLeyenda] = useState('')
  const [errorTitulo, setErrorTitulo] = useState(false)

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    if(!data.get('email').includes('@'))alert('El correo electronico debe ser verdadero')
    if(data.get('password').length < 8) alert('La contrasenia debe contener al menos 8 caracteres')

    dispatch(sendLoginRequest({
      email: data.get('email'),
      password: data.get('password'),
    }))
    .then((resp)=>{console.log(resp)})



  };

  return (
    <>
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
          >
          <Avatar sx={{ m: 1, bgcolor: '#1e244b' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Iniciar sesión
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  onChange={(e)=>{
                    setTitulo(e.target.value)
                    if(titulo.length>45){
                      setErrorTitulo(true)
                      setLeyenda('El titulo contiene mas de 45 caracteres')
                    }else{
                      setErrorTitulo(false)
                      setLeyenda('')
                    }
                  }}
                  error={errorTitulo}
                  required
                  fullWidth
                  label="Correo Electronico"
                  name="email"
                  autoComplete="email"
                  helperText={leyenda}
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label="Contraseña"
                  type="password"
                  name="password"
                  autoComplete="new-password"
                  helperText={'Debe contener al menos 8 caracteres'}
                  />
              </Grid>
            </Grid>
            <Button
              fullWidth
              type="submit"
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Iniciar sesión
            </Button>
            <Grid container justifyContent="flex-end">
            </Grid>
          </Box>
        </Box>
      </Container>
    </ThemeProvider>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    <br/>
    </>
  );
}
